import React from 'react';
import Table from '@material-ui/core/Table';
import TableHeaderModule from './TableHeaderModule';
import TableBodyModule from './TableBodyModule';
import axios from 'axios';

//let counter = 0;
//function createData(name, last_active, gender, class_year, team, meals_logged_per_day, calories, protein, fat, carbs) {
//    counter += 1;
//    return {id: counter, name, last_active, gender, class_year, team, meals_logged_per_day, calories, protein, fat, carbs};
//}

export default class TableModule extends React.Component {
    counter = 0;

    getUsers = () => {
        axios.get(
            'http://127.0.0.1:5000/api/getUsers',
            {
                headers: {'Content-type': 'application/json'}
            }
        ).then((data) => {
            let details = data['data'];
            details.map((n) => {
                const full_name = n.firstname + " " + n.lastname;
                n['fullname'] = full_name;
            });

            console.log(data);

            this.setState({
                data: data['data']
            })
        })


        //return users;
    }

    /*([
        createData('Paulo Frazão', '1 day ago', 'Male', 2020, 'Men\'s Bowling', 3, '2000/2200', '105/135', '70/85', '170/200'),
            createData('Jamie Mercurio', '2 day ago', 'Male', 2020, 'Men\'s Running', 3, '2001/2200', '104/135', '7/85', '17/200'),
            createData('Abby Breitfeld', '3 day ago', 'Female', 2020, 'Women\'s Running', 3, '2002/2200', '100/135', '70/85', '10/200'),

    ]) */

    state = {
        entries: 0,
        orderBy: 'name',
        order: 'asc',
        data: [],
        restrictions: {}
        /*[
            createData('Paulo Frazão', '1 day ago', 'Male', 2020, 'Men\'s Bowling', 3, '2000/2200', '105/135', '70/85', '170/200'),
            createData('Jamie Mercurio', '2 day ago', 'Male', 2020, 'Men\'s Running', 3, '2001/2200', '104/135', '7/85', '17/200'),
            createData('Abby Breitfeld', '3 day ago', 'Female', 2020, 'Women\'s Running', 3, '2002/2200', '100/135', '70/85', '10/200'),
        ]*/
    };

    componentDidMount() {
        this.getUsers();
    }


    handleRequestSort = (event, property) => {
        const orderBy = property;
        let order = 'desc';

        if (this.state.orderBy === property && this.state.order === 'desc') order = 'asc';

        this.setState({order, orderBy})
    };

    handleFilterRequest = (field, value) => {
        console.log(this.state.restrictions);
        let rest = this.restrictions;

        // value is in restrictions, so removes it, and potentially field too
        if (field in rest && rest[field].includes(value)) {
            const index = rest[field].indexOf(value);
            rest[field].splice(index, 1);

            if (rest[field].length === 0) {
                delete rest[field];
            }
        }

        // value is not in restrictions, so adds it
        else if (field in rest && !rest[field].includes(value)) {
            rest[field].push(value);
        }

        // field isn't in restrictions, so adds it and value
        else {
            rest.field = value;
        }

        this.setState({
            restrictions: rest
        });
    } 

    render() {
        return (
            <Table>
                <TableHeaderModule 
                    orderBy={this.state.orderBy} 
                    order={this.state.order}
                    onRequestSort={this.handleRequestSort}
                    onFilter={this.handleFilterRequest}
                />
                <TableBodyModule 
                    data={this.state.data}
                    onRequestSort={this.handleRequestSort}
                    orderBy={this.state.orderBy}
                    order={this.state.order}
                />
            </Table>
        );
    }
}
